const navBtn=document.querySelector(".nav__btn"),soundBtn=document.querySelector(".headers__sound-btn"),headerVideo=document.querySelectorAll(".header__video");for(let e=0;e<headerVideo.length;e++)headerVideo[e].addEventListener("mouseenter",function(t){headerVideo[e].play()}),headerVideo[e].addEventListener("mouseout",function(t){headerVideo[e].pause()});const handleNav=()=>{const e=document.querySelector(".nav__list"),t=document.querySelector(".nav__overlay");document.querySelectorAll(".nav__list").forEach(n=>{n.addEventListener("click",()=>{e.classList.remove("active"),t.classList.remove("active"),navBtn.classList.remove("active")})}),e.classList.toggle("active"),navBtn.classList.toggle("active"),t.classList.toggle("active"),t.addEventListener("click",()=>{e.classList.remove("active"),navBtn.classList.remove("active"),t.classList.remove("active")})},musicToggle=()=>{const e=document.querySelector(".headers__video");e.muted=!e.muted,soundBtn.classList.toggle("active")};navBtn.addEventListener("click",handleNav),soundBtn.addEventListener("click",musicToggle);